SPDataList.common:CreateProcedureSQL:
create procedure dbo.USR_USP_DATALIST_USR_INVENTORYPROCESS ( @CURRENTAPPUSERID uniqueidentifier = null ) as set nocount on;
SELECT IP.ID, IP.NAME, IDS.NAME as IDSETREGISTERNAME, BPV.CAPTION as QUERYVIEWCAPTION, IP.DESCRIPTION, BPI.OWNERID FROM dbo.USR_INVENTORYPROCESS IP INNER JOIN dbo.IDSETREGISTER IDS on IP.IDSETREGISTERID = IDS.ID INNER JOIN BusinessProcessView BPV on IP.QUERYVIEWID = BPV.QUERYVIEWCATALOGID
LEFT JOIN dbo.BUSINESSPROCESSINSTANCE BPI on IP.ID = BPI.BUSINESSPROCESSPARAMETERSETID where dbo.UFN_SECURITY_APPUSER_GRANTED_BUSINESSPROCESSINSTANCE_IN_SYSTEMROLE(@CURRENTAPPUSERID, IP.ID) = 1 order by IP.NAME
