SPSimpleList.common:CreateProcedureSQL:
create procedure dbo.USR_USP_SIMPLEDATALIST_FoodLowInvBPRuns as
/*select ID as VALUE, CODETABLENAME as LABEL from dbo.TABLE1 order by CODETABLENAME */
SELECT TOP (20) dbo.BUSINESSPROCESSOUTPUT.ID as VALUE , dbo.USR_INVENTORYPROCESS.NAME + ' (' + CONVERT(VARCHAR(20), dbo.BUSINESSPROCESSSTATUS.STARTEDON, 100) + ')' as LABEL FROM dbo.BUSINESSPROCESSOUTPUT INNER JOIN dbo.BUSINESSPROCESSSTATUS ON dbo.BUSINESSPROCESSOUTPUT.BUSINESSPROCESSSTATUSID = dbo.BUSINESSPROCESSSTATUS.ID INNER JOIN dbo.USR_INVENTORYPROCESS ON dbo.BUSINESSPROCESSSTATUS.BUSINESSPROCESSPARAMETERSETID = dbo.USR_INVENTORYPROCESS.ID WHERE (dbo.USR_INVENTORYPROCESS.QUERYVIEWID = 'C4EA9FE3-F272-4DE8-B6E9-53622A28EBED') AND (dbo.BUSINESSPROCESSSTATUS.STATUS = 'COMPLETED') ORDER BY dbo.BUSINESSPROCESSSTATUS.STARTEDON DESC
