SQLTranslationFunction.common:CreateFunctionSQL:
create function dbo.USR_UFN_TRANSLATIONFUNCTION_LowInvBPRuns(@ID uniqueidentifier) returns nvarchar(50) as begin declare @s nvarchar(100);
SELECT @s = dbo.USR_INVENTORYPROCESS.NAME + ' (' + CONVERT(VARCHAR(20), dbo.BUSINESSPROCESSSTATUS.STARTEDON, 100) + ')' FROM dbo.BUSINESSPROCESSOUTPUT INNER JOIN dbo.BUSINESSPROCESSSTATUS ON dbo.BUSINESSPROCESSOUTPUT.BUSINESSPROCESSSTATUSID = dbo.BUSINESSPROCESSSTATUS.ID INNER JOIN dbo.USR_INVENTORYPROCESS ON dbo.BUSINESSPROCESSSTATUS.BUSINESSPROCESSPARAMETERSETID = dbo.USR_INVENTORYPROCESS.ID WHERE dbo.BUSINESSPROCESSOUTPUT.ID = @ID
return @s; end
