SPDataList.common:CreateProcedureSQL:
create procedure dbo.USR_USP_DATALIST_FOODITEMSHORTAGE as set nocount on;
WITH LOWINVENTORY_CTE (FOODBANKID, FOODITEMID, ITEMCOUNT) AS ( SELECT
FBTH.FOODBANKID, FBTD.FOODITEMID, sum(case when FBTH.FOODBANKTXTYPECODE = 0 then FBTD.QUANTITY else FBTD.QUANTITY * -1 end) as FOODITEMCOUNT FROM dbo.USR_FOODBANKTXHEADER FBTH JOIN dbo.USR_FOODBANKTXDETAIL FBTD on FBTH.ID = FBTD.FOODBANKTXHEADERID JOIN USR_FOODITEM FI on FBTD.FOODITEMID = FI.ID GROUP BY FBTH.FOODBANKID, FBTD.FOODITEMID ) SELECT TOP 6 CTE.FOODITEMID, FI.NAME as FOODITEMNAME, SUM(FI.LOWINVENTORYTHRESHOLD - CTE.ITEMCOUNT) as INVENTORYSHORTAGE from LOWINVENTORY_CTE CTE join dbo.USR_FOODITEM FI on CTE.FOODITEMID = FI.ID where CTE.ITEMCOUNT < FI.LOWINVENTORYTHRESHOLD GROUP BY CTE.FOODITEMID, FI.NAME ORDER BY INVENTORYSHORTAGE DESC
